import React, { useState } from 'react';
import Books from './pages/Books'
import Library from './pages/Library'
import './App.css';
import { Nav } from "react-bootstrap";

const PAGE_SEARCH = 'search';
const PAGE_CART = 'cart';

function App() {
  const [cart, setCart] = useState([]);
  const [page, setSearch] = useState(PAGE_SEARCH);

  const navigateTo = (nextPage) => {
    setSearch(nextPage);
  };

  const getCartTotal = () => {
    return cart.reduce(
      (sum, { quantity }) => sum + quantity,
      0
    );
  };

  return (
    <div className="App">
      <header>
        <Nav bg="light" expand="lg" defaultActiveKey="/home" as="ul">
          <Nav.Link style={{color: "white"}} href="/home">Google Books</Nav.Link>
          <Nav.Link style={{color: "white"}} eventKey="link-1">Home</Nav.Link>
          <Nav.Link style={{color: "white"}} eventKey="link-2">Library</Nav.Link>
        </Nav>
      </header>
      {page === PAGE_SEARCH && (
        <Books cart={cart} setCart={setCart} />
      )}
      {page === PAGE_CART && (
        <Library cart={cart} setCart={setCart} />
      )}
    </div>
  );
}

export default App;
