import React, { Component } from 'react';
import SearchArea from '../components/SearchArea/SearchArea';
import request from 'superagent'
import BookList from '../components/BookList/BookList'
import {Card, Button, Row, Col, Container} from 'react-bootstrap'

class Books extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      searchField: ''
    }
  }

  searchBook = (e) => {
    e.preventDefault();
    request
      .get("https://www.googleapis.com/books/v1/volumes")
      .query({ q: this.state.searchField })
      .then((data) => {
        console.log(data);
        this.setState({ books: [...data.body.items] })
      })
  }

  handleSearch = (e) => {
    console.log(e.target.value)
    this.setState({ searchField: e.target.value })
  }

  render() {
    return (
      <div>
        <SearchArea searchBook={this.searchBook} handleSearch={this.handleSearch} />
        <div className='List' style={{ display: 'flex', flexWrap: 'wrap' }}>
          {
            this.state.books.map((book, i) => {
              return (
                // <div className='card-container'>
                //   <img src={book.volumeInfo.imageLinks.thumbnail} alt="" />
                //   <div className='desc'>
                //     <h2>{book.volumeInfo.title}</h2>
                //     <h3>{book.volumeInfo.author}</h3>
                //     <p>{book.volumeInfo.publishedDate}</p>
                //     <button>Add to Cart</button>
                //   </div>
                // </div>
                <Container style={{ display: 'flex', margin: '30px 0 30px'}} fluid>
                  <img src={book.volumeInfo.imageLinks.thumbnail} />
                  <Card style={{ width: '100%'}}>
                  <Card.Body>
                    <Card.Title>S{book.volumeInfo.title}</Card.Title>
                    <Card.Text>
                    {book.volumeInfo.description}
                    </Card.Text>
                    <Button variant="primary">Add to Library</Button>
                  </Card.Body>
                  </Card>
                </Container>
              )
            })
          }
        </div>
      </div>
    )
  }
}

export default Books;
