import React from 'react';
import { Form, Button, Container } from "react-bootstrap";

const SearchArea = (props) => {
    return (
      <Container>
        <Form className="float" onSubmit={props.searchBook} action="">
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Search for Books</Form.Label>
            <Form.Control onChange={props.handleSearch} type="text" type="text" placeholder="Enter email" />
            <Button className="float-right" variant="primary" type="submit">Search</Button>
          </Form.Group>

        </Form>
      </Container>
    )
  }
  

export default SearchArea;
